    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        String box1=(String)jComboBox1.getSelectedItem();
        String box2=(String)jComboBox2.getSelectedItem();
        if(box1.equals("Celcius") && box2.equals("Farenheit"))
        { 
           double c = Double.parseDouble(jTextField2.getText());
           double f = (float)((c*9/5)+32);
           jTextField3.setText(String.valueOf(f));
        }
        
        if(box1.equals("Celcius") && box2.equals("Kelvin"))
        {   
            double c = Double.parseDouble(jTextField2.getText());
            double k=c+273.15;
            jTextField3.setText(String.valueOf(k));
        }
        if(box1.equals("Farenheit") && box2.equals("Celcius"))
        {   
            double f=Double.parseDouble(jTextField2.getText());
            double c=((f-32)/(float)9)*5;
            jTextField3.setText(String.valueOf(c));
        }
        if(box1.equals("Farenheit") && box2.equals("Kelvin"))
        {   
           double f=Double.parseDouble(jTextField2.getText());
           double k=(((f-32)/(float)9)*5)+273.15;
           jTextField3.setText(String.valueOf(k));
        }
        if(box1.equals("Kelvin") && box2.equals("Farenheit"))
        {   
            double k=Double.parseDouble(jTextField2.getText());
            double c=(k-273.15);
            double f=(float)((c*9/5)+32);
            jTextField3.setText(String.valueOf(f));
        }
        if(box1.equals("Farenheit") && box2.equals("Celcius"))
        {   
            double k=Double.parseDouble(jTextField2.getText());
            double c=k-273.15;
            jTextField3.setText(String.valueOf(c));
        }
        if(box1.equals("Celcius") && box2.equals("Celcius"))
        {   
            double c=Double.parseDouble(jTextField2.getText());
            jTextField3.setText(String.valueOf(c));
        }
        if(box1.equals("Farenheit") && box2.equals("Farenheit"))
        {   
            double f=Double.parseDouble(jTextField2.getText());
            jTextField3.setText(String.valueOf(f));
        }
        if(box1.equals("Kelvin") && box2.equals("Kelvin"))
        {   
            double k=Double.parseDouble(jTextField2.getText());
            jTextField3.setText(String.valueOf(k));
        }
    }//GEN-LAST:event_jButton2ActionPerformed

